mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, status = #{status}, description = #{description}, image = #{image}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select s.*,c.name categoryName from setmeal s left join category c on s.category_id = c.id
and s.name like concat('%',#{name},'%')
and s.status = #{status}
and s.category_id = #{categoryId}
order by s.create_time desc

select * from setmeal

    
        and name like concat('%',#{name},'%')
    
    
        and category_id = #{categoryId}
    
    
        and status = #{status}

select count(id) from setmeal

    
        and status = #{status}
    
    
        and category_id = #{categoryId}
